import React, { useState , useEffect } from 'react';
import { useToastMessage } from '../context/error-context';
import Pagination from '../components/pagination';
import Modal from '../components/modal';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

function formatDate(dateString: string) {
    const date = new Date(dateString);
    const formattedDate = date.toLocaleDateString('en-GB');
    return formattedDate;
}

const Home: React.FC= () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => setIsModalOpen(!isModalOpen);

  const [selectedYear, setSelectedYear] = useState<string>('2022');
  const yearlyData: Record<string, number[]> = {
    2022: [20, 25, 30, 28, 35, 32, 40, 38, 45, 42, 50, 48],
    2023: [22, 27, 35, 33, 40, 38, 45, 42, 50, 48, 55, 52],
    2024: [25, 30, 38, 36, 43, 41, 48, 46, 53, 51, 58, 56],
  };

  const updateChartData = (year: string) => {
    setSelectedYear(year);
  };

  const data = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"],
    datasets: [{
      label: 'Movies Watched',
      data: yearlyData[selectedYear], 
      backgroundColor: '#FF6E33',
      borderColor: '#DF3F10',
      borderWidth: 1
    }]
  };

  const options = {
    scales: {
      y: {
        // beginAtZero: true,
      },
    },
  };

  const { showToast } = useToastMessage();
  const [responseActivityData, setResponseActivityData] = useState<any>({});
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [startIndex, setStartIndex] = useState(0);
  const limit = 10

  const fetchActivityData = async (page: number, limit: number) => {
    try {

      const storedToken = localStorage.getItem('token');
      const response = await fetch(`http://127.0.0.1:8080/user/activity?limit=${limit}&page=${page}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${storedToken}` 
        },
      });

      if (!response.ok) {
        const errorMessage = await response.json();
        throw new Error(errorMessage["message"]);
      }

      const jsonResponse = await response.json();
      setResponseActivityData(jsonResponse);
      setTotalPages(jsonResponse.total);
      setStartIndex((page - 1) * limit + 1);
    } catch (error: any) {
      showToast(error.message, { type: 'error' });
    }
  };

  useEffect(() => {
    fetchActivityData(currentPage, limit); 
  }, [currentPage]);

  const handlePageChange = (page: number) => {
    setCurrentPage(page);
  };

  const [user, setUser] = useState<any>({});
  useEffect(() => {
    const currentUser = async () => {
        try {
            const storedToken = localStorage.getItem('token');
            const response = await fetch('http://127.0.0.1:8080/user/me', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${storedToken}` 
                },
            });

            if (!response.ok) {
                const errorMessage = await response.json();
                throw new Error(errorMessage["message"]);
            }

            const data = await response.json();
            console.log(data)
            setUser(data)
        } catch (error: any) {
            showToast(error.message, { type: 'error' });
        }
    };

    currentUser(); 
}, []); 

return (
    <>
    <Modal isOpen={isModalOpen} toggleModal={toggleModal} />
    <div className="border bg-card w-full max-w-3xl mx-auto">
      {/* <div className="flex flex-col space-y-1.5 p-6 pb-4">
        <div className="flex justify-end">
          <button onClick={toggleModal} className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 bg-black text-white hover:bg-black/90 h-10 px-4 py-2">
            Connect Netflix
          </button>
        </div>

        <div className="absolute text-left">
          <div className="flex font-semibold whitespace-nowrap leading-none tracking-tight">
            <div className="pt-4 pr-2 pb-2">
              <svg width="34px" height="34px" strokeWidth="1.5" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" color="#000000">
                <path d="M12 11.5C14.2091 11.5 16 9.70914 16 7.5C16 5.29086 14.2091 3.5 12 3.5C9.79086 3.5 8 5.29086 8 7.5C8 9.70914 9.79086 11.5 12 11.5Z" stroke="#000000" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"></path>
                <path d="M7 20.5C9.20914 20.5 11 18.7091 11 16.5C11 14.2909 9.20914 12.5 7 12.5C4.79086 12.5 3 14.2909 3 16.5C3 18.7091 4.79086 20.5 7 20.5Z" stroke="#000000" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"></path>
                <path d="M17 20.5C19.2091 20.5 21 18.7091 21 16.5C21 14.2909 19.2091 12.5 17 12.5C14.7909 12.5 13 14.2909 13 16.5C13 18.7091 14.7909 20.5 17 20.5Z" stroke="#000000" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"></path>
              </svg>
            </div>
            <h2 className="pt-5 text-xl">Data Aggregator</h2>
          </div>
          <p className="text-sm text-muted-foreground text-gray-500 dark:text-gray-400">
            Welcome back, {user && user.username}! Let's see what you have been up to.
          </p>
        </div>
      </div> */}

      <div className="bg-gray-100">
        <div className="relative flex flex-col space-y-1.5 p-6 pb-4 ">
            <div className="flex bg-white max-w-40 ">
                <div className="pr-2">
                    <img src={user && user.avatar_url} alt="User avatar" className="rounded-full  object-cover" width={30} height={30} />
                </div>
                <h3 className="mt-2 text-lg text-gray-800 text-sm">{user && user.username}</h3>
            </div>
            <div className="absolute right-0 mr-4 flex justify-end">
                <button onClick={toggleModal} className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 bg-black text-white hover:bg-black/90 h-10 px-4 py-2">
                    Connect Netflix
                </button>
            </div>
        </div>

        <div className="p-4 ">
            <div className="flex font-semibold whitespace-nowrap leading-none tracking-tight">
