import { useState } from 'react';

interface PaginationProps {
  total: number;
  limit: number;
  currentPage: number;
  onPageChange: (page: number) => void;
}

const Pagination: React.FC<PaginationProps> = ({ total, limit, currentPage, onPageChange }) => {
  const totalPages = Math.ceil(total / limit); 

  const getPageNumbers = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }
    return pageNumbers;
  };

  const handlePageChange = (page: number) => {
    if (page !== currentPage) {
      onPageChange(page);
    }
  };

  return (
    <nav className="flex justify-center my-4">
      <ul className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
        {currentPage > 1 && (
          <li>
            <a href="#" onClick={() => handlePageChange(currentPage - 1)} className="relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
              Previous
            </a>
          </li>
        )}
        {getPageNumbers().map((page) => (
          <li key={page}>
            <a href="#" onClick={() => handlePageChange(page)} className={`relative inline-flex items-center px-4 py-2 border ${currentPage === page ? 'bg-gray-500 text-white' : 'bg-white text-gray-700'} border-gray-300 text-sm font-medium hover:bg-gray-50`}>
              {page}
            </a>
          </li>
        ))}
        {currentPage < totalPages && (
          <li>
            <a href="#" onClick={() => handlePageChange(currentPage + 1)} className="relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
              Next
            </a>
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Pagination;
